    getStyle = () => {
        return{
            background: '#f4f4f4',
            padding: '10px',
            borderBottom: '1px #ccc dotted',
            textDecoration: this.props.todo.completed ? 'line-through' : 'none',
        }
    } 

    render() {
        return (
            <div style={this.getStyle()}>
                <input type="checkbox" name="" id="" />
                <p>{ this.props.todo.title }</p>
            </div>
        )
    }
}

//PropTypes
Todoitem.propTypes = {
    todo: PropTypes.object.isRequired
}

